        public static List<SelectListItem> GetCities()
        {
            List<SelectListItem> CityList = new List<SelectListItem>();

            IdentityProfileAppContext entities = new IdentityProfileAppContext();
            foreach (City city in entities.City.ToList())
            {
                CityList.Add(new SelectListItem { Text = city.Name, Value = city.Id.ToString() });
            }
            return CityList;
        }

        public static List<SelectListItem> GetDistricts()
        {
            List<SelectListItem> DistrictList = new List<SelectListItem>();

            IdentityProfileAppContext entities = new IdentityProfileAppContext();
            foreach (District district in entities.District.ToList())
            {
                DistrictList.Add(new SelectListItem { Text = district.Name, Value = district.Id.ToString() });
            }
            return DistrictList;
        }

        public static List<SelectListItem> GetDistrictsByCityId(int cityId)
        {
            List<SelectListItem> DistrictList = new List<SelectListItem>();
            IdentityProfileAppContext entities = new IdentityProfileAppContext();
            foreach (District district in entities.District.Where(d => d.CityId == cityId).ToList())
            {
                DistrictList.Add(new SelectListItem { Text = district.Name, Value = district.Id.ToString() });
            }
            return DistrictList;
        }
